DDDDLSRC.SOURCE:
@AbapCatalog.sqlViewName: 'ZSMB_I_SO_UN'
@AbapCatalog.compiler.compareFilter: true
@AbapCatalog.preserveKey: true
@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Unmanaged SalesOrder Item Model.'

define root view ZSMB_I_SALESORDER_UN 
   as select from snwd_so as SalesOrder 
  
   composition [0..*] of ZSMB_I_SALESORDERITEM_UN as _SalesOrderItem
   
   association [0..1] to ZSMB_I_BUSINESSPARTNER_UN as _Customer     on $projection.BuyerGuid = _Customer.NodeKey
   association [1..1] to Sepm_I_SalesOrdOverallStatusT  as _OverallStatus  on $projection.OverallStatus   = _OverallStatus.SalesOrderOverallStatus                                                                      and $projection.Language      = _OverallStatus.Language
{ 
    key SalesOrder.node_key   as Node_Key,
        SalesOrder.so_id      as SalesOrderID,  
        SalesOrder.created_at as Created_At,      
        SalesOrder.changed_at as Changed_At,   
        @Semantics.currencyCode: true
        SalesOrder.currency_code    as CurrencyCode,  
        @Semantics.amount.currencyCode: 'CurrencyCode'   
        SalesOrder.gross_amount     as GrossAmount,  
        @Semantics.amount.currencyCode: 'CurrencyCode'  
        SalesOrder.net_amount       as NetAmount,       
        @Semantics.amount.currencyCode: 'CurrencyCode'   
        SalesOrder.tax_amount       as TaxAmount,
        case SalesOrder.overall_status
        when 'I' then 0      //In Progress: Grey Color
        when 'P' then 0      //Paid:  Grey Color       
        when 'X' then 1      //Canceled: Red Color
        when ' ' then 2      //Empty: Yellow color     
        when 'N' then 2      //New: Yellow Color
        when 'D' then 3      //Delivered: Green Color
        else 0 
        end as Criticality,
        $session.system_language as Language,        
        SalesOrder.overall_status as OverallStatus,
        SalesOrder.buyer_guid as BuyerGuid,
        concat_with_space(_Customer.CompanyName, 
                          _Customer.LegalForm,
                          1) as CustomerName, 
        /* Associations */   
        _SalesOrderItem,  
        _OverallStatus,        
        _Customer
}
